css:
.invoice-container {
  font-family: 'Lato', 'Noto Sans Tamil', sans-serif;
  max-width: 800px;
  margin: auto;
  padding: 20px;
  color: #333;
  background: #fff;
}

h2, h3, h4 {
  text-align: center;
  margin-bottom: 10px;
  color: #2c3e50;
}

p {
  margin: 4px 0;
  font-size: 14px;
}

.table {
  width: 100%;
  border-collapse: collapse;
  margin-top: 15px;
}

.table th, .table td {
  border: 1px solid #ccc;
  padding: 8px;
  text-align: left;
  font-size: 13px;
}

.table th {
  background-color: #f2f2f2;
  font-weight: bold;
}

hr {
  border: none;
  border-top: 1px solid #aaa;
  margin: 20px 0;
}

em {
  display: block;
  text-align: center;
  margin-top: 20px;
  font-style: italic;
  color: #555;
}

@media print {
  body {
    margin: 0;
    padding: 0;
    background: none;
  }
  .invoice-container {
    box-shadow: none;
    page-break-inside: avoid;
  }
}
